import {MediaGrid} from '@ataskit/media-filmstrip';

const identifiers = [{id: 'media-id-1'}, {id: 'media-id-2'}]

<MediaGrid 
  isInteractive
  context={mediaContext} 
  items={identifiers} 
/>

export default (config: Config) => {
  const app = new Koa();

  // Provides ctx.config in routes
  app.context.config = config;

  // Provides ctx.logger and ctx.tracer
  attachLoggingAndTracing(app);

  // Provides ctx.asapGenerator
  attachAsapGenerator(app, config.asap);

  // Provides ctx.metrics (via StatsD)
  attachStatsDMetrics(app, config.metrics);

  // Add request-scoped cancellation
  app.use(cancellationMiddleware);

  // Count number of incoming requests
  app.use(requestCounterMiddleware);

  // OpenAPI request and response validation
  app.use(oas({
    file: `${__dirname}/../../openapi.yaml`,
    endpoint: '/openapi.json',
    uiEndpoint: '/api.html',
    validatePaths: ['/api'],
    validateResponse: process.env.NODE_ENV === 'production' ? false : true
  }));

  // Attach all our routes
  app.use(routes);

  return app;
};
